import React, { useEffect, useState } from "react";
import "./Result.css";

export default function Result({ searchCriteria }) {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCars = async () => {
      setLoading(true);
      setError(null);

      try {
        if (!searchCriteria || Object.keys(searchCriteria).length === 0) {
          console.warn("No search criteria provided");
          return;
        }

        const queryParams = new URLSearchParams(searchCriteria).toString();
        const url = `http://localhost:3002/cars${queryParams ? `?${queryParams}` : ""}`;
        console.log("Fetching from:", url);

        const response = await fetch(url);
        if (!response.ok) {
          throw new Error("Failed to fetch cars");
        }

        const data = await response.json();
        console.log("Fetched data:", data);

        setCars([...data]); // 确保状态更新
      } catch (error) {
        setError(error.message);
        console.error("Error fetching cars:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchCars();
  }, [JSON.stringify(searchCriteria)]); // 依赖项改为字符串，确保触发更新

  if (loading) {
    return <div className="loading">Loading cars...</div>;
  }

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  if (!cars || cars.length === 0) {
    return <div className="no-results">No cars found matching your criteria</div>;
  }

  return (
    <div className="result-container">
      <h2 className="text-xl font-bold mb-4">Car List</h2>
      <table className="result-table">
        <thead>
          <tr>
            <th>Make</th>
            <th>Model</th>
            <th>Year</th>
            <th>Description</th>
            <th>Odometer (km)</th>
            <th>Vehicle Condition</th>
            <th>Sale Location</th>
            <th>Sale Category</th>
            <th>Salvage Vehicle</th>
            <th>Sale Date</th>
            <th>Sale Price</th>
          </tr>
        </thead>
        <tbody>
          {cars.map((car, index) => (
            <tr key={index}>
              <td>{car.make}</td>
              <td>{car.model}</td>
              <td>{car.year}</td>
              <td>{car.description}</td>
              <td>{car.odometer}</td>
              <td>{car.car_condition}</td>
              <td>{car.states}</td>
              <td>{car.sales_category}</td>
              <td>{car.salvage_vehicle ? "Yes" : "No"}</td>
              <td>{car.sale_date}</td>
              <td>{car.sale_price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
