import React, { useState } from "react";
import "./Search.css"; // Import styles

export default function Search({ onSearch }) {
    const [searchCriteria, setSearchCriteria] = useState({
        make: "",
        model: "",
        year: "",
        state: "",
        carCondition: "",
        salesCategory: "",
        bodyType: "",
        fuelType: "",
        engine: "",
        transmission: "",
        odometer: "",
        saleDate: "",
        customDate: "",
        badges: "",
        cylinders: "",
        division: "",
        drive: "",
        seats: "",
        doors: ""
    });

    const [showMoreFilters, setShowMoreFilters] = useState(false);

    const options = {
        make: ["Audi", "BMW", "Mercedes", "Toyota"],
        model: ["A5", "X5", "C-Class", "Camry"],
        year: ["2015", "2016", "2017", "2018"],
        state: ["Victoria", "New South Wales", "Western Australia", "Tasmania", "Queensland","South Australia","Australian Capital Territory"],
        carCondition: ["New", "Used", "Certified Pre-Owned"],
        salesCategory: ["Private", "Dealer"],
        bodyType: ["Sedan", "Sportback", "SUV", "Truck"],
        fuelType: ["Gasoline", "Diesel", "Electric", "Hybrid"],
        engine: ["2.0 Turbo", "3.0 V6", "4.0 V8"],
        transmission: ["Automatic", "Manual"],
        odometer: ["0-50k", "50k-100k", "100k-150k"],
        saleDate: ["Last Week", "Last Month", "Last Year"],
        customDate: ["2024-02-07", "2024-01-01", "2023-12-01"],
        badges: ["Quattro", "M Performance", "AMG"],
        cylinders: ["4", "6", "8"],
        division: ["Luxury", "Sports", "Economy"],
        drive: ["FWD", "RWD", "AWD"],
        seats: ["2", "4", "5", "7"],
        doors: ["2", "4", "5"]
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setSearchCriteria({
            ...searchCriteria,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSearch(searchCriteria); // 调用 onSearch 回调函数
    };

    // 获取所有搜索字段，并按是否展开决定显示数量
    const criteriaKeys = Object.keys(searchCriteria);
    const visibleFields = showMoreFilters ? criteriaKeys : criteriaKeys.slice(0, 8);

    // **分组成 4 个一行**
    const groupedFields = [];
    for (let i = 0; i < visibleFields.length; i += 4) {
        groupedFields.push(visibleFields.slice(i, i + 4));
    }

    return (
        <div className="search-container">
            <h2>Car Search</h2>
            <form onSubmit={handleSubmit}>
                <div className="search-fields">
                    {groupedFields.map((row, rowIndex) => (
                        <div className="search-row" key={rowIndex}>
                            {row.map((key) => (
                                <div className="search-field" key={key}>
                                    <label>{key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}</label>
                                    <select name={key} value={searchCriteria[key]} onChange={handleInputChange} className="search-select">
                                        <option value="">Select {key}</option>
                                        {options[key] && options[key].map((option) => (
                                            <option key={option} value={option}>{option}</option>
                                        ))}
                                    </select>
                                </div>
                            ))}
                        </div>
                    ))}
                </div>
                <button type="button" className="more-filters-button" onClick={() => setShowMoreFilters(!showMoreFilters)}>
                    {showMoreFilters ? "Show Less Filters" : "More Filters"}
                </button>
                <button type="submit" className="search-button">Search</button>
            </form>
        </div>
    );
}
