import React, { useState, useEffect } from "react";
import Result from "./Result";
import "./Search.css";

export default function Search() {
    const [searchCriteria, setSearchCriteria] = useState({
        make: "",
        model: "",
        year: "",
        state: "",
        carCondition: "",
        salesCategory: "",
        bodyType: "",
        fuelType: "",
        engine: "",
        transmission: "",
        odometer: "",
        saleDate: "",
        customDate: "",
        badges: "",
        cylinders: "",
        division: "",
        drive: "",
        seats: "",
        doors: ""
    });

    const [showMoreFilters, setShowMoreFilters] = useState(false);
    const [cars, setCars] = useState([]);

    useEffect(() => {
        fetch("http://localhost:3002/cars")
            .then(response => response.json())
            .then(data => setCars(data))
            .catch(error => console.error("Error fetching cars:", error));
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setSearchCriteria({
            ...searchCriteria,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Search Criteria:", searchCriteria);
        // 这里可以根据 searchCriteria 过滤数据
    };

    const handleClear = () => {
        setSearchCriteria({
            make: "",
            model: "",
            year: "",
            state: "",
            carCondition: "",
            salesCategory: "",
            bodyType: "",
            fuelType: "",
            engine: "",
            transmission: "",
            odometer: "",
            saleDate: "",
            customDate: "",
            badges: "",
            cylinders: "",
            division: "",
            drive: "",
            seats: "",
            doors: ""
        });
    };

    return (
        <div className="search-container">
            <h2>Car Search</h2>
            <form onSubmit={handleSubmit}>
                <div className="search-fields">
                    {Object.keys(searchCriteria).slice(0, showMoreFilters ? undefined : 8).map((key) => (
                        <div className="search-field" key={key}>
                            <label>{key.replace(/([A-Z])/g, " $1").replace(/^./, str => str.toUpperCase())}</label>
                            <select name={key} value={searchCriteria[key]} onChange={handleInputChange} className="search-select">
                                <option value="">Select {key}</option>
                                {/* 这里需要根据 key 填充选项 */}
                            </select>
                        </div>
                    ))}
                </div>
                <div className="search-actions">
                    <button type="button" className="more-filters-button" onClick={() => setShowMoreFilters(!showMoreFilters)}>
                        {showMoreFilters ? "Show Less Filters" : "More Filters"}
                    </button>
                    <div className="right-buttons">
                        <button type="submit" className="search-button">Search</button>
                        <button type="button" className="clear-button" onClick={handleClear}>Clear</button>
                    </div>
                </div>
            </form>
            <Result cars={cars} />
        </div>
    );
}
