import React, { useEffect, useState } from "react";
import "./Result.css";

export default function Result({ searchCriteria }) {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCar, setSelectedCar] = useState(null);

  useEffect(() => {
    const fetchCars = async () => {
      setLoading(true);
      setError(null);
      try {
        const queryParams = new URLSearchParams(searchCriteria).toString();
        const response = await fetch(`http://localhost:3002/car?${queryParams}`);
        if (!response.ok) throw new Error("Failed to fetch cars");
        const data = await response.json();
        setCars(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };
    fetchCars();
  }, [searchCriteria]);

  return (
    <div className="result-container">
      {loading && <div>Loading...</div>}
      {error && <div>Error: {error}</div>}
      {cars.length === 0 && <div>No cars found</div>}
      {cars.map((car, index) => (
        <div key={index} onClick={() => setSelectedCar(car)}>
          {car.make} {car.model} ({car.year})
        </div>
      ))}
      {selectedCar && (
        <div className="modal">
          <div className="modal-content">
            <h2>{selectedCar.make} {selectedCar.model}</h2>
            <button onClick={() => setSelectedCar(null)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}
